package com.jspider.accessmodifiers;

public class DreamFire {
	private static int a=10;
	static int b=20;
	protected static int c=30;
	public static int d=40;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(DreamFire.a);
		System.out.println(DreamFire.b);
		System.out.println(DreamFire.c);
		System.out.println(DreamFire.d);
	}

}
